\[ u(x) =  \begin{cases}     1, x = 1\\     (-1)^k, x = a_1^{p1}\times a_2^{p2}\times a_3^{p3} ... \times a_k^{pk}\\     0, 其他情况\\ \end{cases} \] <pre><code>void init(int n)
{
u[1]=1;
for(int i=2;i&lt;=n;i++)
{
if(!vis[i]) prime[++cnt]=i,u[i]=-1;
for(int j=1;prime[j]*i&lt;=n;j++)
{
vis[prime[j]*i]=true;
if(i%prime[j]==0)
{
u[prime[j]*i]=0;
break;
}
u[prime[j]*i]=u[i]*(-1);
}
}
}
